import React, {useState, useEffect} from 'react'
import axios from 'axios'

export const Repositories = ({repositories}) => {
  const getRepositoryCount = async(item) => {
    try {
        const response = await axios.get(item.repos_url);
        if(response.ok) {
            return response.data.length;
        }
    }
    catch(error) {
        console.error('Error fetching Repo URL')
    }
  }

  useEffect(() => {
    const fetchRepositoryCounts = async () => {
      try {
        for (const repo of repositories) {
          const repoCount = await getRepositoryCount(repo);
          repo.repo_count = repoCount;
        }
      } catch (error) {
        console.error('Error fetching repository counts:', error);
      }
    };

    fetchRepositoryCounts();
  }, [repositories]);
  return (
    <ul className="w-2/3 mt-5 m-auto bg-neutral-900 border-2 border-neutral-800 text-neutral-400 select-none p-8 rounded-lg">
        {repositories.map(repo => (
        <li className='w-full border-2 cursor-pointer transition-all hover:text-neutral-300 mb-2 rounded-xl p-4' key={repo.id}>
            <a className="font-bold" target="_blank" rel = 'noopener'>
            {repo.login}
            </a>
        </li>
        ))}
  </ul>
  )
}

export default Repositories;
