import React, {useState, useEffect} from 'react'
import axios from 'axios'

export const Repositories = async ({repositories}) => {
    useEffect(() => {
        const fetchRepositoryCounts = async () => {
          for (const repo of repositories) {
            try {
              const response = await axios.get(repo.repos_url);
              if (response.status === 200) {
                repo.repo_count = response.data.length;
              } else {
                repo.repo_count = 'N/A';
              }
            } catch (error) {
              console.error(`Error fetching repositories for ${repo.login}:`, error);
              repo.repo_count = 'N/A';
            }
          }
        };

        fetchRepositoryCounts();
      }, [repositories]);
  return (
    <ul className="w-2/3 mt-5 m-auto bg-neutral-900 border-2 border-neutral-800 text-neutral-400 select-none p-8 rounded-lg">
        {repositories.map(repo => (
        <li className='w-full border-2 cursor-pointer transition-all hover:text-neutral-300 mb-2 rounded-xl p-4' key={repo.id}>
            <a className="font-bold" target="_blank" rel = 'noopener'>
            {repo.login} - {repo.repo_count !== undefined ? repo.repo_count : 'Loading...'} repositories
            </a>
        </li>
        ))}
  </ul>
  )
}

export default Repositories;
