import React from "react";
import {useState, useEffect} from 'react'
import Repositories from "./Repositories";
import Pagination from './Pagination';
import axios from 'axios'

function App() {
  const [query, setQuery] = useState('');
  const [repositories, setRepositories] = useState([]);

  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(4);

  const searchRepositories = async () => {
    const response = await axios.get('http://localhost:4000/api/searchrepositories', {
      params: {
        q: query,
        sort: 'stars',
        order: 'desc'
      }
    });
    setRepositories(response.data.items);
    setLoading(false)
  }

  const lastIndex = currentPage * postsPerPage;
  const startIndex = lastIndex - postsPerPage;
  const currentPosts = repositories.slice(startIndex, lastIndex);

  const paginate = pageNumber = setCurrentPage(pageNumber);

  return (
    <div className="w-full bg-neutral-900 h-screen">
      <h1 className="text-4xl text-center m-auto pt-5 font-bold text-neutral-200 select-none">Project Practice</h1>
      <div className="search-container flex justify-center w-full">
        <input type = "text" value = {query} className="w-1/3 border-2 rounded-xl block mt-5 bg-neutral-900 border-neutral-800 p-2 text-neutral-300" placeholder="Search..." onChange={(e) => setQuery(e.target.value)}></input>
        <button onClick={searchRepositories} className="Search mt-5 px-8 ml-2 block rounded-xl bg-neutral-800 text-neutral-200 hover:bg-neutral-700 transition-all">Search</button>
      </div>
      <Repositories repositories={currentPosts} loading={loading}/>
      <Pagination postsPerPage={postsPerPage} totalPosts={repositories.length} paginate={paginate}/>
    </div>
  );
}

export default App;
